import java.io.BufferedReader;import java.io.File;import java.io.FileOutputStream;import java.io.FileReader;import java.io.PrintStream;import java.util.ArrayList;import utils.FileFinder;public class baseline {	public static void main(String args[]) throws Exception{		ArrayList<File> files = FileFinder.GetAllFiles("text/CRF/testdata", ".txt", true);		for (File f : files){			String path = f.getPath().replaceAll("testdata", "baselinetest");			path = path.replaceAll("output", "test");			path = path.replaceAll("txt", "data");			System.out.println(path);			@SuppressWarnings("resource")			PrintStream	ps = new PrintStream(new FileOutputStream(path));			BufferedReader br = new BufferedReader(new FileReader(f));			String line = null;			while ((line = br.readLine()) != null) {				String[] split = line.split("	");				if (split.length < 2) continue;				String annotated = split[split.length-2];				String word = split[0];				ps.println(word + " " + annotated);				}			br.close();			}	}}