import java.io.BufferedReader;import java.io.File;import java.io.FileOutputStream;import java.io.FileReader;import java.io.PrintStream;import java.util.ArrayList;import utils.FileFinder;public class breakline {	public static void main(String[] args) throws Exception{		ArrayList<File> files = FileFinder.GetAllFiles("/home/lizhou/Desktop/nursing", "", true);		for(File f: files){			String name = f.getName();			@SuppressWarnings("resource")			PrintStream	ps = new PrintStream(new FileOutputStream("/home/lizhou/Desktop/nursing_1/" + name));			BufferedReader br = new BufferedReader(new FileReader(f));			String line = null;			while ((line = br.readLine()) != null) {				String[] split = line.split(" +");				if (split.length < 2) continue;				for(int i = 0; i < split.length; i++){					ps.println(split[i]);					System.out.println(split[i]);				}				}			br.close();		}	}}