import java.io.BufferedReader;import java.io.File;import java.io.FileReader;import java.util.ArrayList;import java.util.HashMap;import java.util.Iterator;import java.util.Map.Entry;import utils.FileFinder;public class count {	private HashMap<String,Integer> labels;	private String inputFolder;		public count(String string) {		inputFolder = string;		labels = new HashMap<String, Integer>();	}	public static void main(String[] args) throws Exception{		count count = new count(args[0]);		ArrayList<File> files = FileFinder.GetAllFiles(count.inputFolder,				".data", true);				for(File f : files){			@SuppressWarnings("resource")			BufferedReader br = new BufferedReader(new FileReader(f));			String line = null;			while ((line = br.readLine()) != null) {				String[] split = line.split(" ");				if (split.length < 2)					continue;								String label = split[split.length-1];				if(count.labels.containsKey(label)){					int num = count.labels.get(label);					count.labels.put(label, num + 1);				} else{					count.labels.put(label, 1);				}			}		}				Iterator<Entry<String, Integer>> iter = count.labels.entrySet().iterator();		while (iter.hasNext()) {		    Entry<String, Integer> entry = iter.next();	    		    		    System.out.println(entry.getKey() + "	 :" + entry.getValue());		}			}}